ESX = nil


Citizen.CreateThread(function()
	while not ESX do

		TriggerEvent("esx:getSharedObject", function(library) 
			ESX = library 
		end)

		Citizen.Wait(0)
	end

	for blipIndex, blipValues in pairs(Config["locations"]) do
		if blipIndex == "entering" then
			local blip = AddBlipForCoord(blipValues["coords"])
			SetBlipSprite (blip, 487)
			SetBlipDisplay(blip, 4)
			SetBlipScale  (blip, 0.9)
			SetBlipColour (blip, 76)
			SetBlipAsShortRange(blip, true)
			BeginTextCommandSetBlipName("STRING")
			AddTextComponentString(Strings["blip"])
			EndTextCommandSetBlipName(blip)
		end
	end

	while true do

		
		local sleep = 1000
		local playerPed = PlayerPedId()
		local coords = GetEntityCoords(playerPed)
		for place,value in pairs(Config["locations"]) do
			if not IsPedInAnyVehicle(playerPed) and not IsEntityDead(playerPed) and GetDistanceBetweenCoords(coords, value["coords"], true) <= 50 then
				sleep = 5
				if place == "entering" then
					DrawAdvancedMarker({["type"] = 6, ["pos"] = value["coords"] - vector3(0, 0, 0.88), ["r"] = 50, ["g"] = 200, ["b"] = 200, ["sizeX"] = 1.0, ["sizeY"] = 1.0, ["sizeZ"] = 1.0, ["rotate"] = true})
					if GetDistanceBetweenCoords(coords, value["coords"], true) <= 1 then
						help(Strings["press_e_enter"])
						if IsControlJustReleased(0, 38) then
							DoScreenFadeOut(500)
							Wait(600)
							SetEntityCoords(playerPed, Config["locations"]["exit"]["coords"] - vector3(0, 0, 0.98)) SetEntityHeading(playerPed, Config["locations"]["exit"]["heading"])
							Wait(200)
							DoScreenFadeIn(500)
						end
					end

				elseif place == "exit" then
					DrawAdvancedMarker({["type"] = 6, ["pos"] = value["coords"] - vector3(0, 0, 0.88), ["r"] = 50, ["g"] = 200, ["b"] = 200, ["sizeX"] = 1.0, ["sizeY"] = 1.0, ["sizeZ"] = 1.0, ["rotate"] = true})
					if GetDistanceBetweenCoords(coords, value["coords"], true) <= 1 then
						help(Strings["press_e_exit"])
						if IsControlJustReleased(0, 38) then
							DoScreenFadeOut(500)
							Wait(600)
							SetEntityCoords(playerPed, Config["locations"]["entering"]["coords"] - vector3(0, 0, 0.98)) SetEntityHeading(playerPed, Config["locations"]["entering"]["heading"])
							Wait(200)
							DoScreenFadeIn(500)
						end
					end

				elseif place == "insurancemenu" then
					if ESX.PlayerData["job"] and ESX.PlayerData["job"]["name"] == "forsakring" then
						DrawAdvancedMarker({["type"] = 6, ["pos"] = value["coords"] - vector3(0, 0, 0.88), ["r"] = 50, ["g"] = 200, ["b"] = 200, ["sizeX"] = 1.0, ["sizeY"] = 1.0, ["sizeZ"] = 1.0, ["rotate"] = true})
						if GetDistanceBetweenCoords(coords, value["coords"], true) <= 1 then
							help(Strings["press_e_insurancemenu"])
							if IsControlJustReleased(0, 38) then
								openInsuranceMenu()
							end
						end
					end
				elseif place == "storage" then
					if ESX.PlayerData["job"] and ESX.PlayerData["job"]["name"] == "forsakring" then
						DrawAdvancedMarker({["type"] = 6, ["pos"] = value["coords"] - vector3(0, 0, 0.88), ["r"] = 50, ["g"] = 200, ["b"] = 200, ["sizeX"] = 1.0, ["sizeY"] = 1.0, ["sizeZ"] = 1.0, ["rotate"] = true})
						if GetDistanceBetweenCoords(coords, value["coords"], true) <= 1 then
							help(Strings["press_e_storage"])
							if IsControlJustReleased(0, 38) then
								OpenStorage(false)
							end
						end
					end
				end
			end
		end
		Wait(sleep)
	end
end)

RegisterNetEvent("esx:playerLoaded")
AddEventHandler("esx:playerLoaded", function(playerData)
	ESX.PlayerData = playerData
end)

RegisterNetEvent("esx:setJob")
AddEventHandler("esx:setJob", function(newJob)
	ESX.PlayerData["job"] = newJob
end)

RegisterNetEvent('esx_phone:loaded')
AddEventHandler('esx_phone:loaded', function(phoneNumber, contacts)
	local specialContact = {
		name       = 'Folksam',
		number     = 'forsakring',
		base64Icon = 'data:image/png;base64,'
	}

	TriggerEvent('esx_phone:addSpecialContact', specialContact.name, specialContact.number, specialContact.base64Icon)
end)